import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';


@Component({
  selector: 'app-hello-world',
  templateUrl: './hello-world.component.html',
  styleUrls: ['./hello-world.component.css']
})


export class HelloWorldComponent implements OnInit {

  title: string = "Hello";
  matrix_board: number[][] = [];


  ngOnInit(): void {
    for (let row = 0; row < 6; row++) {
      this.matrix_board.push([])

      for (let column = 0; column < 7; column++) {
        this.matrix_board[row].push(0);
      }
    }

    console.log("ON INIT")
  }

  constructor(private http: HttpClient) { }


  sendPostRequest(url: string, data: any): Observable<Object> {
    return this.http.post(url, data);
  }

  sendReqFlask() {


    let api_url: string = "http://localhost:5000";
    let post_data: any = {
      "data": { "answer": 1 },
      "matrix_board": this.matrix_board
    };

    this.sendPostRequest(api_url, post_data).subscribe(
      (response: Object) => {
        console.log('Success!', response);

        //Update Matrix
        let response_data = Object.values(response);

        console.log(response_data[0]["matrix_board"])

        this.matrix_board = response_data[0]["matrix_board"]

      },
      (error) => {
        console.error('Error:', error);
      }
    )

  }
}
