import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';



@Component({
  selector: 'app-hello-world',
  templateUrl: './hello-world.component.html',
  styleUrls: ['./hello-world.component.css']
})


export class HelloWorldComponent implements OnInit {

  matrix_board: number[][] = [];
  data_form = { answer: '1' };
  msg_to_show = ""
  show_button: boolean = true

  //Token Class
  tokens_to_add: any[] = [];

  constructor(private http: HttpClient) { }

  //Fill Matrix
  ngOnInit(): void {
    for (let row = 0; row < 6; row++) {
      this.matrix_board.push([])

      for (let column = 0; column < 7; column++) {
        this.matrix_board[row].push(0);
      }
    }
  }


  //SEND REQUESTS METHOD
  sendPostRequest(url: string, data: any): Observable<Object> {
    return this.http.post(url, data);
  }



  onSubmit() {

    let api_url: string = "http://localhost:5000";
    let post_data: any = {
      "data": { "answer": this.data_form.answer },
      "matrix_board": this.matrix_board
    };

    //SEND REQUEST TO FLASK
    this.sendPostRequest(api_url, post_data).subscribe({

      next: (response) => this.parseSuccess(response),
      error: (e) => console.error(e),
      complete: () => console.info("complete")

    })

  }

  parseSuccess(response: any): boolean {

    //CHECK HUMAN
    //Check if token insertion is not successful
    if (response.human.result == false) {
      console.log(response.human.msg);
      this.msg_to_show = response.human.msg
      return false
    }

    //Update board
    this.matrix_board = response.human.matrix_board

    let position_left = 31 + response.human.last_move[1] * 87
    let position_top = 32 + response.human.last_move[0] * 87

    let token_to_add = {
      "color": "yellow",
      "column": response.human.last_move[1],
      "row": response.human.last_move[0],
      "token_id": response.human.token_id,
      "position_left": position_left + "px",
      "position_top": position_top + "px"
    }

    this.tokens_to_add.push(token_to_add)

    this.msg_to_show = response.human.msg


    if (response["human"]["is_game_draw"] == true || response["human"]["is_game_won"] == true) {
      this.show_button = false
    }


    return true








  }

}
