import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';


@Component({
  selector: 'app-hello-world',
  templateUrl: './hello-world.component.html',
  styleUrls: ['./hello-world.component.css']
})


export class HelloWorldComponent implements OnInit {

  title: string = "Hello";
  matrix_board: number[][] = [];

  api_url: string = "http://127.0.0.1:5000";
  post_data: any = {
    "data": { "answer": 1 },
    "matrix_board": this.matrix_board
  };

  ngOnInit(): void {
    this.matrix_board = []
    for (let row = 0; row < 6; row++) {
      this.matrix_board.push([])

      for (let column = 0; column < 7; column++) {
        this.matrix_board[row].push(0);
      }
    }
  }

  constructor(private http: HttpClient) { }

  sendPostRequest(url: string, data: any): Observable<Object> {
    return this.http.post(url, this.post_data);
  }

  sendReqFlask() {
    this.sendPostRequest(this.api_url, this.post_data).subscribe(
      (response) => {
        console.log('Success!', response);
      },
      (error) => {
        console.error('Error:', error);
      }
    )

  }
}
